package day0110;

class Ipgo {
	private String sangName;
	private int price;

	// set메서드
	public void setName(String sangName) {
		this.sangName = sangName;
	}

	public void setPrice(int price) {
		this.price = price;
	}

	// get메서드
	public String getName() {
		return sangName;
	}

	public int getPrice() {
		return price;
	}
}

public class SangpumIpgo_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Ipgo ip = new Ipgo();
		ip.setName("사과");
		ip.setPrice(4000);
		String name1 = ip.getName();
		int price1 = ip.getPrice();
		System.out.println(name1 + " " + price1 + "원");

		Ipgo ip2 = new Ipgo();
		ip2.setName("배");
		ip2.setPrice(5000);
		String name2 = ip2.getName();
		int price2 = ip2.getPrice();
		System.out.println(name2 + " " + price2 + "원");

	}

}
